sp_widget.client_script:
api.controller=function($rootScope) {
	var c = this;

	$rootScope.$on('selectItem', function (event, object) {
		c.server.get({
			action: 'select',
			sysId: object.sysId
		}).then(function (r) {
			c.data = r.data;
		});
	});
};

